goog.provide("goog.html.utils"),goog.require("goog.string"),goog.html.utils.stripHtmlTags=function(g){return goog.string.unescapeEntities(goog.string.trim(g.replace(goog.html.utils.HTML_TAG_REGEX_,function(g,t){return goog.html.utils.INLINE_HTML_TAG_REGEX_.test(t)?"":" "}).replace(/[\t\n ]+/g," ")))},goog.html.utils.INLINE_HTML_TAG_REGEX_=/^(?:abbr|acronym|address|b|em|i|small|strong|su[bp]|u)$/i,goog.html.utils.HTML_TAG_REGEX_=/<[!\/]?([a-z0-9]+)([\/ ][^>]*)?>/gi;